import React,{useState,useEffect} from "react";
import axios from "axios";
import useCookie from '../Components/useCookie'
import MenuNav from "../Components/MenuNav";
import CartProdCard from "../Components/CartProdCard";

import prod1 from '../pics/prods/1.jpg';

function Cart() {
    const uidCookie = useCookie('userid');
    const [prodList, setProdList] = useState([]);
    useEffect(() => {
        axios.get(`http://localhost:3001/cart?userid=${uidCookie}`).then((res) => {
            setProdList(res.data)
        });
    }, []);

    return (
        <div className="cart">
            <MenuNav />
            <div className="head">
                <h4>Корзина</h4>
                <div className="count">1</div>
            </div>
            <div className="body">
                <div className="products">
                    <div className="prodList">
                        {prodList.map((prod)=>{
                        <CartProdCard img={require(`../pics/prods/${prod.Prod.image}`)} name={prod.Prod.name} weight="0.19" number="1" price="8.19" isAvail={true} />
                        })}
                        <CartProdCard img={prod1} name="sadasd" weight="0.19" number="1" price="8.19" isAvail={true} />
                        <CartProdCard img={prod1} name="sadasd" weight="0.19" number="1" price="8.19" isAvail={true} />
                    </div>
                </div>
                <aside>
                    <div className="cheque">
                        <h4>Итого:</h4>
                        <div className="priceCount">
                            <p>Товары (<span className="prodsCount">1</span>)</p>
                            <p className="price">22 BYN</p>
                        </div>
                        <div className="deliveryPrice">
                            <p>Доставка</p>
                            <p>бесплатно</p>
                        </div>
                        <div className="total">
                            <p>К оплате</p>
                            <h3>32 BYN</h3>
                        </div>
                        <div>
                        <button type="submit">Оформить заказ</button>
                        </div>
                    </div>
                </aside>
            </div>
        </div>
    );
}

export default Cart;
