import React, { useState } from "react";
import axios from 'axios';

function ReportRemains() {
    const [exportType, setExportType] = useState('xlsx');

    const saveReportRemains = () => {
        switch (exportType) {
            case 'xlsx': {
                axios.get(`http://localhost:3001/excel/exportToExcel/remains`, { responseType: 'blob' })
                    .then(response => {
                        const url = window.URL.createObjectURL(response.data);
                        const a = document.createElement('a');
                        a.href = url;
                        let today = new Date();
                        today = formatDate(today);
                        a.download = `Отчёт остатков ${today}.xlsx`;
                        document.body.appendChild(a);
                        a.click();
                        a.remove();
                    })
                    .catch(error => console.error(error));
                break;
            }
            case 'pdf': {
                axios.get(`http://localhost:3001/pdf/exportToPdf/remains`, { responseType: 'blob' })
                    .then(response => {
                        const url = window.URL.createObjectURL(response.data);
                        const a = document.createElement('a');
                        a.href = url;
                        let today = new Date();
                        today = formatDate(today);
                        a.download = `Отчёт остатков ${today}.pdf`;
                        document.body.appendChild(a);
                        a.click();
                        a.remove();
                    })
                    .catch(error => console.error(error));
                break;
            }
        }
    }
    function formatDate(date) {
        var dd = date.getDate();
        if (dd < 10) dd = '0' + dd;

        var mm = date.getMonth() + 1;
        if (mm < 10) mm = '0' + mm;

        var yy = date.getFullYear();

        return dd + '.' + mm + '.' + yy;
    }

    return (
        <>
            <h5>Отчёт по остаткам</h5>
            <div className="btns">
                <span>Отчёт по всем товарам</span>
                <button className="mainbtn" onClick={() => saveReportRemains('pdf')}>Скачать</button>
            </div>
        </>
    );
}

export default ReportRemains;
