import React, { useState } from "react";
import MenuNav from "../Components/MenuNav";

import edit from '../pics/change_icon.svg';

function confirm() {

}

function Authorization() {
    const [editable, setEditable] = useState(false);
    const [confirmed, setConfirmed] = useState(false);
    const [email, setEmail] = useState(null);
    const handleFirstSubmit = (event) => {
        event.preventDefault();
        if (document.getElementById("email").value.length > 0) {
            setEmail(document.getElementById("email").value);
            setConfirmed(true);
        }
    };
    const forbidCyrrilic = () => {
        const re = /[а-яё]/gi;
        if (re.test(this.value))
            this.value = this.value.replace(re, '');
    }

    return (
        <div className="authorization">
            <MenuNav />
            <div className="authForm">
                {!confirmed ?
                    <form onSubmit={handleFirstSubmit}>
                        <h4>Вход/Регистрация</h4>
                        <label htmlFor="email">Введите электронную почту</label>
                        <input type="email" id="email" placeholder="example@gmail.com" onInput={e => forbidCyrrilic} />
                        <button type="submit">Войти</button>
                    </form> : ''}
                {confirmed ?
                    <form className="confirm">
                        <h4>Подтверждение электронной почты</h4>
                        <label htmlFor="email">Введите электронную почту, чтобы получить код подтверждения</label>
                        <div className="input">
                            <input type="email" id="emailC" disabled={!editable} value={email} onChange={e => setEmail(e.target.value)} onInput={e => forbidCyrrilic} />
                            <img src={edit} onClick={() => setEditable(!editable)} />
                        </div>
                        <input type="number" size="4" placeholder="Введите код" />
                        <p>Сообщение с кодом подтверждения отправлено на вашу почту.</p>
                        <button class="resend">Отправить код повторно</button>
                        <button type="submit">Подтвердить</button>
                    </form> : ''}
            </div>
        </div>
    );
}

export default Authorization;
