import React, { useState, useEffect } from "react";
import axios from 'axios';
import useCookie from '../Components/useCookie'
import { useParams } from 'react-router-dom';

import MenuNav from "../Components/MenuNav";

import user from '../pics/user.svg';
import date from '../pics/date.svg';

function Order() {
    const uidCookie = useCookie('userid');
    let { code } = useParams();
    const [user, setUser] = useState();
    const [prod, setProd] = useState([]);
    useEffect(() => {
        axios.get(`http://localhost:3001/orders/orderGroup/${id}`).then((res) => {
            let array = res.data;
            let list = array.map(obj => {
                let date = new Date(obj.createdAt);
                obj.createdAt = formatDate(date);
                return obj;
            });
            setProd(list);
        });

        axios.get(`http://localhost:3001/users?id=${id}`).then((res) => {
            setUser(res.data[0])
            console.log(res.data[0])
            setAccess(res.data[0].Admins[0].accessLvl)
        });
    }, []);

    function formatDate(date) {

        var dd = date.getDate();
        if (dd < 10) dd = '0' + dd;

        var mm = date.getMonth() + 1;
        if (mm < 10) mm = '0' + mm;

        var yy = date.getFullYear() % 100;
        if (yy < 10) yy = '0' + yy;

        return dd + '.' + mm + '.' + yy;
    }

    
    return (
        <div className="order">
            <MenuNav />
            <div className="top">
                <div>
                    <a className="back" href={`/me/${uidCookie}`}>
                        Назад
                    </a>
                </div>
                <div>
                    <div className="header">
                        <h4>Заказ №{code}</h4>
                    </div>
                </div>
            </div>
            <div className="body">
                <aside>
                    <ul className="asideFilter">
                        <li>
                            <img src={user} />
                            <div>
                                <b>Получатель</b>
                                <span>email</span>
                            </div>
                        </li>
                        <li>
                            <img src={date} />
                            <div>
                                <b>Дата заказа</b>
                                <span>11.11.1111</span>
                            </div>
                        </li>
                    </ul>
                </aside>
            </div>
        </div>
    );
}

export default Order;
