import React, { useState, useEffect } from "react";
import axios from "axios";
import MenuNav from "../Components/MenuNav";
import GeneralPageHeader from "../Components/GeneralPageHeader";
import AnimalTypesFilter from "../Components/AnimalTypesFilter";
import ProductCard from "../Components/ProductCard";

function Catalog({ name, animal, type }) {
    const [prodList, setProdList] = useState([]);
    useEffect(() => {
        axios.get(`http://localhost:3001/prods/${animal}/${type}`).then((res) => {
            setProdList(res.data)
            console.log(res.data)
        });
    }, []);
    let animalBC;
    switch(animal){
        case 'dog': animalBC = 'Собаки'; break;
        case 'cat': animalBC = 'Собаки'; break;
        case 'bird': animalBC = 'Собаки'; break;
        case 'rodent': animalBC = 'Собаки'; break;
        case 'fish': animalBC = 'Собаки'; break;
        case 'ap': animalBC = 'Собаки'; break;
    }
    return (
        <div className="sale">
            <MenuNav />
            <GeneralPageHeader name={name} count='625' ways={[{ name: 'Главная', href: '/' }, { name: 'Акции', href: '/sales' }]} />
            <div className="general">
                <aside>
                    <AnimalTypesFilter />
                </aside>
                <div className="prods">
                    {prodList.map((prod) => (
                        <ProductCard key={prod.id} img={require(`../pics/prods/${prod.image}`)} href="/product" name={`${prod.brand} ${prod.name}`} packing={prod.Feed.weight} price={prod.price} onSale={false}/>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Catalog;
