import React, { useState, useEffect } from "react";
import axios from "axios";
import MenuNav from "../Components/MenuNav";
import GeneralPageHeader from "../Components/GeneralPageHeader";
import AnimalTypesFilter from "../Components/AnimalTypesFilter";
import ProductCard from "../Components/ProductCard";

function Catalog({ name, animal, type }) {
    const [prodList, setProdList] = useState([]);
    useEffect(() => {
        axios.get(`http://localhost:3001/prods/${animal}/${type}`).then((res) => {
            setProdList(res.data)
            console.log(res.data)
        });
    }, []);
    let animalBC;
    switch (animal) {
        case 'dog': animalBC = 'Собаки'; break;
        case 'cat': animalBC = 'Кошки'; break;
        case 'bird': animalBC = 'Птицы'; break;
        case 'rodent': animalBC = 'Грызуны'; break;
        case 'fish': animalBC = 'Рыбки'; break;
        case 'apteka': animalBC = 'Ветаптека'; break;
    }
    return (
        <div className="sale">
            <MenuNav />
            <GeneralPageHeader name={name} count={prodList.length} ways={[{ name: 'Главная', href: '/' }, { name: 'Каталог', href: '/' }, { name: animalBC, href: '/' }, { name: name, href: `/catalog/${animal}/${type}` }]} />
            <div className="general">
                <aside>
                    <ul className="asideFilter">
                        <li>
                            <a>Бренд</a>
                            <div className="filterItems">

                            </div>
                        </li>
                        <li>
                            <a>Коты</a>
                            <div className="filterItems">

                            </div>
                            </li>
                        <li>
                            <a>Фасовка</a>
                            <div className="filterItems">

                            </div>
                            </li>
                        <li>
                            <a>Возраст</a>
                            <div className="filterItems">

                            </div>
                        </li>
                        <li>
                            <a>Вкус</a>
                            <div className="filterItems">

                            </div>
                        </li>
                        <li>
                            <a>Ветаптека</a>
                            <div className="filterItems">

                            </div>
                        </li>
                    </ul>
                </aside>
                <div className="prods">
                    {prodList.map((prod) => (
                        <ProductCard key={prod.id} img={require(`../pics/prods/${prod.image}`)} href="/product" name={`${prod.brand} ${prod.name}`} packing={prod.Feed.weight} price={prod.price} onSale={false} />
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Catalog;
