import React, { useState, useEffect } from "react";
import axios from 'axios';
import { useParams } from 'react-router-dom';
import MenuNav from "../Components/MenuNav";

import exit from '../pics/exit.svg';

function Me() {
    let { id } = useParams();
    const [user, setUser] = useState();
    const [page, setPage] = useState('main');
    useEffect(() => {
        axios.get(`http://localhost:3001/users?id=${id}`).then((res) => {
            setUser(res.data)
        });
    }, []);
    return (
        <div className="me">
            <MenuNav />
            <div className="body">
                <aside>
                    <ul>
                        <li onClick={() => () => setPage('main')}><a>Профиль</a></li>
                        <li onClick={() => setPage('favorites')}><a>Избранное</a></li>
                        <li onClick={() => setPage('pets')}><a>Питомцы</a></li>
                        <li onClick={() => setPage('orders')}><a>Заказы</a></li>
                    </ul>
                    <div className="exit">
                        <button>
                            <img src={exit} />
                            Выйти
                        </button>
                    </div>
                </aside>
                <div className="main">
                    {page === 'main' ?
                        <>
                            <h4>Профиль</h4>
                            <div className="inf">
                                <div className="general">

                                </div>
                                <div className="additional">

                                </div>
                            </div>
                        </>
                        : page === 'favorites' ?
                        <>
                        <h4>Избранное</h4>
                        </>
                        : page === 'pets' ?
                        <>
                        <h4>Питомцы</h4>
                        </>
                        :
                        <>
                        <h4>Заказы</h4>
                        </>
                    }
                </div>
            </div>
        </div>
    );
}

export default Me;
