import React, { useState, useEffect } from "react";
import axios from "axios";
import useCookie from '../Components/useCookie'
import MenuNav from "../Components/MenuNav";
import CartProdCard from "../Components/CartProdCard";
import emptyCart from '../pics/emptyCart.png';

function Cart() {
    const uidCookie = useCookie('userid');
    const [prodList, setProdList] = useState([]);
    const [total, setTotal] = useState(null);
    const [sale, setSale] = useState(0);
    useEffect(() => {
        axios.get(`http://localhost:3001/cart?userid=${uidCookie}`).then((res) => {
            setProdList(res.data)
        });

        axios.get(`http://localhost:3001/cart/sum/${uidCookie}`).then((res) => {
            setTotal(res.data.total)
        });
    }, []);
    useEffect(() => {
        if (total > 200)
            setSale(10)
        else if (total > 150)
            setSale(7)
        else if (total > 100)
            setSale(5)
    }, [total]);

    const updateTotal = () => {

    }

    return (
        <div className="cart">
            <MenuNav />
            <div className="head">
                <h4>Корзина</h4>
                <div className="count">{prodList.length}</div>
            </div>
            {
                prodList.length < 0 ?
                    <div className="emptyCart">
                        <img src={emptyCart} />
                        <p>Нет товаров</p>
                        <a href='/'>Перейти в каталог</a>
                    </div>
                    :
                    <div className="body">
                        <div className="products">
                            <div className="prodList">
                                {prodList.map((prod) => (
                                    <CartProdCard key={prod.id} id={prod.id} img={require(`../pics/prods/${prod.Prod.image}`)} name={prod.Prod.name} number={prod.number} price={prod.Prod.price} isAvail={prod.Prod.available} />
                                ))}
                            </div>
                        </div>
                        <aside>
                            <div className="cheque">
                                <h4>Итого:</h4>
                                <div className="priceCount">
                                    <p>Товары (<span className="prodsCount">{prodList.length}</span>)</p>
                                    <p className="price">{total} BYN</p>
                                </div>
                                <div className="deliveryPrice">
                                    <p>Скидка</p>
                                    <p>{sale}%</p>
                                </div>
                                <div className="total">
                                    <p>К оплате</p>
                                    <h4>{} BYN</h4>
                                </div>
                                <div>
                                    <button type="submit">Оформить заказ</button>
                                </div>
                            </div>
                        </aside>
                    </div>
            }
        </div>
    );
}

export default Cart;
